import React from 'react';
import { directive } from '@babel/types';


class Lampada extends React.Component{
    lampada = "Apagada";
    change() {
        console.log('Change');
        console.log(this.lampada);
        this.lampada === 'Acesa' ? this.setState = "Apagada" : this.lampada = "Acesa";
    }

    render() {
        return (
            <div 
                className="lampada-cont"
                onClick={() => { this.change() }}>
    <h1>{this.lampada}</h1>
            </div>

        )
    }
}


export default Lampada;
