import React from 'react';
import { directive } from '@babel/types';


class Lampada extends React.Component{
    constructor(props) {
        super(props);
        this.state = {
            lampada: "Apagada"
        }

    }
    change() {
        console.log('Change');
        this.state.lampada === 'Acesa' ? this.setState({lampada: "Apagada"}) : this.setState({lampada: "Acesa"});
        console.log(this.state.lampada);



    }

    render() {
        return (
            <div 
                className="lampada-cont"
                onClick={() => { this.change() }}>
    <h1>{this.state.lampada}</h1>
            </div>

        )
    }
}


export default Lampada;
